                }
            },

            onDelete: function (id) {
                $.post(""Home/Delete"", { id: id });
                hubConnection.invoke(""DeleteNote"", id)
            },

            features: {
                htmlEditor: false
            },
            content: content,
            id: noteId.substring(11),
            posX: posX,
            posY: posY,
            height: height,
            width: width,
            style: {
                backgroundcolor: color,
            }
        })
    });

    hubConnection.on(""ChangeNote"", function (noteId, posX, posY, height, width, color, content) {        
