            width: width,
            style: {
                backgroundcolor: color,
            }
        });
    });

    hubConnection.on(""DeleteNote"", function (id) {
        $(id).postitall('destroy');
    });

    $(document).mousedown(function (e) {
        var border = $('.ui-resizable-handle');
        var note = $("".note"");
        isActive = false;
        if (note.has(e.target).length === 0 && border.has(e.target).length === 0) {
